import React, { useState } from 'react';
import axios from 'axios';
// import './UploadForm.css';

function UploadForm({ setResults }) {
  const [file, setFile] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('/enhance', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setResults(response.data);
    } catch (error) {
      console.error('Error uploading file:', error);
    }
  };

  return (
    <div className="upload-form-container" id="upload">
      <h2 className="text-center">Upload Surveillance Image</h2>
      <form onSubmit={handleSubmit} className="upload-form">
        <input type="file" accept="image/*" onChange={handleFileChange} className="form-control" />
        <button type="submit" className="btn btn-primary mt-3 w-100">Upload and Enhance</button>
      </form>
    </div>
  );
}

export default UploadForm;
